import { useEffect, useState } from "react";
import User from "./User";
import classes from "./UsersList.module.css";

const UsersList = () => {
  const [users, setUsers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState();

  useEffect(() => {
    const fetchUsers = async () => {
      const response = await fetch(
        "https://api.github.com/search/users?q=repos:%3E42+followers:%3E1000"
      );

      if (!response.ok) {
        throw new Error("Something went wrong");
      }

      const data = await response.json();

      //   console.log(data.length);

      const item = await data["items"];

      console.log(data);

      const loadedUsers = [];

      for (let key in item) {
        loadedUsers.push({
          id: key,
          username: item[key].login,
          name: item[key].url,
          bio: data["items"].bio,
          location: data["items"].location,
          mail: data["items"].email,
        });
      }

      setUsers(loadedUsers);
      setIsLoading(false);
    };
    fetchUsers().catch((error) => {
      setIsLoading(false);
      setError(error.message);
    });
  }, []);

  if (isLoading) {
    return (
      <section className={classes.UsersLoading}>
        <p>Loading...</p>
      </section>
    );
  }

  if (error) {
    return (
      <section className={classes.UsersError}>
        <p>{error}</p>
      </section>
    );
  }

  const usersList = users.map((user) => (
    <User
      id={user.id}
      username={user.username}
      name={user.name}
      bio={user.bio}
      location={user.location}
      mail={user.mail}
    />
  ));

  return (
    <section className={classes.users}>
      <ul>{usersList}</ul>
    </section>
  );

  //   fetch("https://api.github.com/search/users?q=location%3Ahmedabad")
  //     .then((response) => response.json())
  //     .then((data) => console.log(data["items"]))
  //     .catch((error) => console.log(error));

  //   fetch("https://api.github.com/users/mihir-1997")
  //     .then((response) => response.json())
  //     .then((data) => console.log(data))
  //     .catch((error) => console.log(error));

  //   return (
  //     <Fragment>
  //       <div>Users</div>
  //     </Fragment>
  //   );
};

export default UsersList;
