import { useEffect, useState } from "react";
import Card from "../UI/Card";
import ErrorModal from "../UI/ErrorModal";
import LoadingSpinner from "../UI/LoadingSpinner";
import User from "./User";
import classes from "./UsersList.module.css";

const UsersList = (props) => {
  const [users, setUsers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState();

  useEffect(() => {
    const fetchUsers = async () => {
      const response = await fetch(
        "https://api.github.com/search/users?q=followers:%3E10&sort=followers&order=desc&per_page=10"
      );

      if (!response.ok) {
        throw new Error("Something went wrong");
      }

      const data = await response.json();
      console.log(data);

      const loadedUsers = [];

      for (let key in data["items"]) {
        const userRes = await fetch(data["items"][key].url);
        if (!userRes.ok) {
          throw new Error("Something went wrong");
        }

        const userdata = await userRes.json();
        //console.log(userdata);

        loadedUsers.push({
          profilepic: userdata.avatar_url,
          username: userdata.login,
          name: userdata.name,
          bio: userdata.bio,
          location: userdata.location,
          mail: userdata.email,
          url: userdata.html_url,
        });
      }

      setUsers(loadedUsers);
      console.log(loadedUsers);
      setIsLoading(false);
    };
    fetchUsers().catch((error) => {
      setIsLoading(false);
      setError(error.message);
    });
  }, []);

  const errorHandler = () => {
    setError(null);
  };

  if (isLoading) {
    return <LoadingSpinner />;
  }

  if (error) {
    return (
      <ErrorModal
        title="Something went wrong!"
        message="Try Again later!"
        onConfirm={errorHandler}
      />
    );
  }

  const usersList = users.map((user) => (
    <User
      profilepic={user.profilepic}
      username={user.username}
      name={user.name}
      bio={user.bio}
      location={user.location}
      mail={user.mail}
      url={user.url}
    />
  ));

  return (
    <section className={classes.users}>
      <ul>{usersList}</ul>
    </section>
  );

  //   fetch("https://api.github.com/search/users?q=location%3Ahmedabad")
  //     .then((response) => response.json())
  //     .then((data) => console.log(data["items"]))
  //     .catch((error) => console.log(error));

  //   fetch("https://api.github.com/users/mihir-1997")
  //     .then((response) => response.json())
  //     .then((data) => console.log(data))
  //     .catch((error) => console.log(error));

  //   return (
  //     <Fragment>
  //       <div>Users</div>
  //     </Fragment>
  //   );

  //   https://github.com/search?o=desc&q=followers%3A%3E%3D1000&ref=searchresults&s=followers&type=Users
};

export default UsersList;
